    @staticmethod
    def isValidStr(string, key=None, inc=False, ignCap=False):
        if not key:
            if not SysMgr.filterGroup:
                return True
            key = SysMgr.filterGroup

        if ignCap:
            string = string.lower()

        for cond in list(key):
            if not cond:
                continue

            cond = str(cond)

            if ignCap:
                cond = cond.lower()

            if inc:
                if cond in string:
                    return True
            else:
                if cond == "*":
                    return True
                elif (
                    cond.startswith("*")
                    and cond.endswith("*")
                    and cond.strip("*") in string
                ):
                    return True
                elif cond.startswith("*") and string.endswith(cond[1:]):
                    return True
                elif cond.endswith("*") and string.startswith(cond[:-1]):
                    return True
                elif cond == string:
                    return True

        return False

