    @staticmethod
    def convSize2Unit(size, isInt=False, unit=None):
        sizeKB = 1024
        sizeMB = 1048576
        sizeGB = 1073741824
        sizeTB = 1099511627776
        sizePB = 1125899906842624
        sizeEB = 1152921504606846976
        # sizeZB = 1180591620717411303424
        # sizeYB = 120892581961462917470617

        # convert to ABS value #
        try:
            sizeAbs = abs(size)
        except SystemExit:
            sys.exit(0)
        except:
            return "?"

        if unit:
            factor = 1 if isInt else 1.0

            upperUnit = unit.upper()
            if upperUnit == "K":
                val = size / sizeKB * factor
            elif upperUnit == "M":
                val = size / sizeMB * factor
            elif upperUnit == "G":
                val = size / sizeGB * factor
            elif upperUnit == "T":
                val = size / sizeTB * factor
            elif upperUnit == "P":
                val = size / sizePB * factor
            elif upperUnit == "E":
                val = size / sizeEB * factor
            else:
                SysMgr.printErr("no support size unit '%s'" % unit)
                sys.exit(-1)

            if isInt:
                num = UtilMgr.convNum(long(val))
            else:
                num = UtilMgr.convNum(round(float(val), 1), isFloat=True)

            return "%s%s" % (num, upperUnit)

        # Int type #
        if isInt:
            try:
                if sizeAbs >= sizeEB:
                    return "%dE" % (size >> 60)
                elif sizeAbs >= sizePB:
                    return "%dP" % (size >> 50)
                elif sizeAbs >= sizeTB:
                    return "%dT" % (size >> 40)
                elif sizeAbs >= sizeGB:
                    return "%dG" % (size >> 30)
                elif sizeAbs >= sizeMB:
                    return "%dM" % (size >> 20)
                elif sizeAbs >= sizeKB:
                    return "%dK" % (size >> 10)
                else:
                    return "%d" % size
            except SystemExit:
                sys.exit(0)
            except:
                return "?"
        # Float type #
        else:
            try:
                if sizeAbs >= sizeEB:
                    return "%.1fE" % ((size >> 50) / 1024.0)
                elif sizeAbs >= sizePB:
                    return "%.1fP" % ((size >> 40) / 1024.0)
                elif sizeAbs >= sizeTB:
                    return "%.1fT" % ((size >> 30) / 1024.0)
                elif sizeAbs >= sizeGB:
                    return "%.1fG" % ((size >> 20) / 1024.0)
                elif sizeAbs >= sizeMB:
                    return "%.1fM" % ((size >> 10) / 1024.0)
                elif sizeAbs >= sizeKB:
                    return "%.1fK" % (size / 1024.0)
                else:
                    return "%d" % (size)
            except SystemExit:
                sys.exit(0)
            except:
                return "?"

    @staticmethod
    def convTime2Unit(sec):
        sec = float(sec)
        conv = UtilMgr.convNum

        try:
            if sec == long(sec):
                return "%ssec" % conv(sec)

            msSec = round(sec * 1000, 3)
            msSecInt = long(msSec)
            if msSec and msSec == msSecInt:
                return "%sms" % msSecInt

            usSec = round(sec * 1000000, 6)
            usSecInt = long(usSec)
            if usSec and usSec == usSecInt:
                return "%sus" % usSecInt

            nsSec = round(sec * 1000000000, 9)
            nsSecInt = long(nsSec)
            if nsSec and nsSec == nsSecInt:
                return "%sns" % nsSecInt

            return sec
        except SystemExit:
            sys.exit(0)
        except:
            return "?"

    @staticmethod
    def convTime2Sec(time):
        # convert time to seconds #
        try:
            sec = 0

            nums = time.strip().split(":")
            if len(nums) == 3:
                y = d = 0
                times = nums
            elif len(nums) == 4:
                y = 0
                d = int(nums[0].rstrip("d"))
                times = nums[1:]
            elif len(nums) == 5:
                y = int(nums[0].rstrip("y"))
                d = int(nums[0].rstrip("d"))
                times = nums[1:]
            else:
                raise Exception()

            # convert type #
            h, m, s = list(map(int, times))

            # convert items to seconds #
            sec += s
            sec += m * 60
            sec += h * 3600
            if d:
                sec += d * 86400
            if y:
                sec += y * 31536000
        except SystemExit:
            sys.exit(0)
        except:
            pass

        return sec

    @staticmethod
    def convTime(time, remainder=False):
        try:
            if not remainder:
                raise Exception("integer")
            remain = ".%02d" % long(time * 100 % 100)
        except SystemExit:
            sys.exit(0)
        except:
            remain = ""

        # convert seconds to time #
        try:
            m, s = divmod(time, 60)
            h, m = divmod(m, 60)

            # hour #
            if h >= 24:
                d, h = divmod(h, 24)

                # year #
                if d >= 365:
                    y, d = divmod(d, 365)
                    d = "%dy:%dd:" % (y, d)
                else:
                    d = "%dd:" % d
            else:
                d = ""

            ctime = "%s%02d:%02d:%02d%s" % (d, h, m, s, remain)
        except SystemExit:
            sys.exit(0)
        except:
            ctime = "%s%02s:%02s:%02s%s" % ("", "?", "?", "?", remain)

        return ctime.strip()

