                        symbol = self.posData[subcall]["symbol"]
                        binary = self.posData[subcall]["binary"]
                        nextCall = " <- %s[%s]" % (symbol, binary)
                        if SysMgr.lineLength > nowLen + len(nextCall):
                            userCall += nextCall
                            nowLen += len(nextCall)
