    @staticmethod
    def loadLib(lib, path=False, inc=False):
        if not SysMgr.importPkgItems("ctypes", False):
            return

        # absolute path #
        if path:
            return CDLL(lib)

        # search ld.so.cache #
        target = SysMgr.findLib(lib, inc)
        if not target:
            target = ["%s.so" % lib]
            ret = FileAnalyzer.getMapFilePath(SysMgr.pid, lib)
            if ret:
                target.append(ret)

        # load libraries #
        for item in target:
            try:
                res = cdll.LoadLibrary(item)
                if res:
                    return res
            except SystemExit:
                sys.exit(0)
            except:
                pass

