    @staticmethod
    def readProcData(tid, path, num=-1):
        path = "%s/%s/%s" % (SysMgr.procPath, tid, path)

        try:
            f = open(path, "r")
        except SystemExit:
            sys.exit(0)
        except:
            SysMgr.printOpenErr(path)
            return None

        if num == -1:
            return f.readlines()
        elif num == 0:
            return f.readline().replace("\n", "")
        else:
            return f.readline().replace("\n", "").split()[num - 1]

