        SysMgr.printTopStats()

        # save DLT data #
        DltAnalyzer.totalTime += SysMgr.uptimeDiff
        DltAnalyzer.msgData.append(UtilMgr.deepcopy(DltAnalyzer.dltData))

        # initialize DLT data #
