        for pid in set(self.prevProcData) - set(self.procData):
            try:
                os.close(self.procFdData.pop(pid, None))
            except SystemExit:
                sys.exit(0)
            except:
                pass

        self.prevProcData = self.procData
        self.procData = {}
        self.fixedProcData = {}
        SysMgr.topInstance = self
        SysMgr.procInstance = self.procData

