    def executeProcess(cmd=None, mute=False, closeAll=True):
        try:
            SystemManager.resetFileTable(mute, closeAll)

            os.execvp(cmd[0], cmd)
        except:
            err = SystemManager.getErrReason()
            SystemManager.printErr(\
                "Fail to execute '%s' because %s" % (' '.join(cmd), err))
