    def convertPidList(procList, isThread=False, exceptMe=False):
        targetList = []

        if not procList and not exceptMe:
            procList = [__module__]

        if not procList:
            return

        # get pids from comm #
        for pid in procList:
            if not pid.isdigit():
                idlist = SystemManager.getPids(pid, isThread)
                if len(idlist) > 0:
                    uniqueList = set(idlist) - set(targetList)
