    @staticmethod
    def checkFilter(comm, pid):
        found = False

        for idx in SystemManager.filterGroup:
            # check exclusion condition #
            if idx.startswith('^'):
                cond = idx[1:]
                if cond in comm or pid == cond:
                    found=False
                    break
                else:
                    found=True
                    continue

            # check inclusion condition #
            if idx in comm > -1 or pid == idx:
                found = True
                break

        return found



