    @staticmethod
    def convStr2Bytes(string):
        # build string #
        res = ''
        for byte in string:
            if type(byte) is str:
                byte = long(repr(struct.unpack('B', byte)[0]))
            bstr = '%x' % byte
            res = '{0:s}{1:0>2} '.format(res, bstr)
        return res


