    @staticmethod
    def readLEB128(fd):
        data = None
        while 1:
            char = fd.read(1)
            if ord(char) & 0x80 == 0:
                break
            elif not data:
                data = char
            else:
                data += char
        return data



    @staticmethod
    def decodeSLEB128(obj):
        size = 1
        value = 0
        for b in obj:
            value = (value << 7) + (ord(b) & 0x7F)
            if (ord(b) & 0x80) == 0:
                break
            size += 1
        obj = obj[:size]
        if ord(obj[-1]) & 0x40:
            # negative -> sign extend
            value |= - (1 << (7 * len(obj)))
        return value, size



    @staticmethod
    def decodeULEB128(obj):
        size = 1
        value = 0

        # get size #
        for b in obj:
            if (ord(b) & 0x80) == 0:
                break
            size += 1

        # decode data #
        for b in reversed(obj[:size]):
            value = (value << 7) + (ord(b) & 0x7F)

        return value, size



