    def doRlimit():
        # get argument #
        if SysMgr.hasMainArg():
            value = SysMgr.getMainArgs()
        elif SysMgr.filterGroup:
            value = SysMgr.filterGroup
        else:
            SysMgr.printErr(
                ("wrong value to change resource limit, "
                "input in the format TID|COMM:RTYPE:SLIM:HLIM"))
            sys.exit(0)

        for item in value:
            conf = item.split(':')
            if len(conf) != 1 and len(conf) != 4:
                SysMgr.printErr(
                    ("wrong value to change resource limit, "
                    "input in the format TID|COMM:RTYPE:SLIM:HLIM"))
