    @staticmethod
    def getTids(
        name, isThread=True, sibling=False,
        main=False, inc=False, cache=False):

        def _checkWait(pidList, waitTime):
            if not waitTime:
                return False

            SysMgr.printWarn(
                "wait for '%s' because no task yet" % name)

            time.sleep(waitTime)

            return True

        # define wait time for target #
        if 'WAITTASK' in SysMgr.environList:
            try:
                waitTime = long(SysMgr.environList['WAITTASK'][0]) / 1000
            except SystemExit:
                sys.exit(0)
            except:
                waitTime = 0.1
        else:
            waitTime = 0

        pidList = []
        prevTargetList = []

        if not SysMgr.isLinux:
            try:
                psutil = SysMgr.getPkg('psutil')
                procs = psutil.process_iter(
                    attrs=['pid', 'name'], ad_value=None)
                for proc in procs:
                    pid = proc.info['pid']
                    comm = proc.info['name']
                    if name == pid:
                        pidList.append(pid)
                    elif UtilMgr.isValidStr(comm, [name]):
                        pidList.append(pid)

                return pidList
            except SystemExit:
                sys.exit(0)
            except:
                SysMgr.printWarn(
                    "failed to find tasks related to '%s'" % name, reason=True)
                return pidList

        while 1:
            # tid #
            if UtilMgr.isNumber(name) and \
                os.path.isdir('%s/%s' % (SysMgr.procPath, name)):
                if sibling:
                    path = '%s/%s/task' % (SysMgr.procPath, name)
                    for pid in os.listdir(path):
                        if pid.isdigit():
                            pidList.append(pid)

                    # check retry condition #
                    if not pidList and _checkWait(pidList, waitTime):
                        continue

                    return pidList
                elif main:
                    return list(set([name, SysMgr.getTgid(name)]))
                else:
                    return [name]

            # comm #
            nameList = [name]

            # set check list #
            targetList = SysMgr.getPidList()
            curList = set(targetList) - set(prevTargetList)
            prevTargetList = targetList

            for pid in curList:
                if not pid.isdigit():
                    continue

                # process #
                if not isThread:
                    # get comm #
                    comm = SysMgr.getComm(pid, cache)
                    if UtilMgr.isValidStr(comm, nameList, inc=inc):
                        pidList.append(pid)
                    continue

                # thread #
                try:
                    threadPath = "%s/%s/task" % (SysMgr.procPath, pid)
                    tids = os.listdir(threadPath)
                except SystemExit:
                    sys.exit(0)
                except:
                    continue

                for tid in tids:
                    if not tid.isdigit():
                        continue

                    # get comm #
                    comm = SysMgr.getComm(tid, cache=cache)
                    if not UtilMgr.isValidStr(comm, nameList, inc=inc):
                        continue

                    # include all siblings #
                    if sibling:
                        pidList += tids
                        break

                    # include the main thread #
                    if main:
                        pidList.append(pid)

                    # include a thread #
                    pidList.append(tid)

            # check retry condition #
            if not pidList and _checkWait(pidList, waitTime):
                continue

            return list(set(pidList))



    @staticmethod
    def mount(source, path, fs, flags=0, data=0):
        try:
            # get ctypes object #
            SysMgr.importPkgItems('ctypes')

            # device #
            if source:
                sourcep = c_char_p(source.encode())
            else:
                sourcep = 0

            # path #
            if path:
                pathp = c_char_p(path.encode())
            else:
                pathp = 0

            # filesystem #
            if fs:
                fsp = c_char_p(fs.encode())
            else:
                fsp = 0

            # call mount syscall #
            ret = SysMgr.syscall('mount', sourcep, pathp, fsp, flags, data)
            if ret != 0:
                SysMgr.printWarn(
                    'failed to mount(%s, %s, %s, %s, %s)' % \
                        (source, path, fs, flags, data))
            return ret
        except SystemExit:
            sys.exit(0)
        except:
            SysMgr.printWarn(
                'failed to call mount(%s, %s, %s, %s, %s)' % \
                    (source, path, fs, flags, data), True, True)



