                # check exit condition #
                if ret < 0:
                    sys.exit(0)

                # send stop signal to target #
                os.kill(long(pid), stopSig)

                SysMgr.printStat(
                    'sent %s to %s(%s) to stop profiling' % \
                        (ConfigMgr.SIG_LIST[stopSig], comm, pid))
